INSERT INTO film (titel, score, genre, taal, duur, datum, beschrijving, youtube_trailer_id, foto, type) VALUES
    ('Maid in Manhattan', 5.3, 'romantiek', 'Engels' , '105 minuten', '13-12-2002', 'Een vrouw begint een baan als kamermeisje in een luxe hotel. Ze wordt verliefd op een beoogde opvolger van een politieke familie. Hij denkt met een vrouw uit hoge kringen te maken te hebben, waardoor ze al snel inzicht krijgt in het leven van een man die ze normaal gesproken alleen van een afstand beoordeeld zou hebben.', 'https://www.youtube.com/embed/v1dv-skGIYY', 'maid_in_manhattan.jpg', 'normaal'),
    ('Think Like a Man', 6.5, 'romantiek', 'Engels' , '123 minuten','20-4-2012' , 'De levens van vier jonge mannen komen volledig op zijn kop te staan nadat hun geliefde vrouwen het boek hebben gekocht van Harvey, een relatie expert, wiens advies zij maar al te graag opvolgen. Probleem is alleen dat Harvey zelf moeite heeft om zijn relatie op orde te krijgen. Als de mannen gezamenlijk lucht van het zaakje krijgen besluiten ze de rollen om te draaien en de vrouwen flink terug te pakken.', 'https://www.youtube.com/embed/vxTN4eX4Vss', 'think_like_a_man.jpg', 'normaal'),
    ('The notebook', 7.8, 'romantiek', 'Engels' , '123 minuten', '7-11-2004', 'Allie Hamilton komt begin jaren 40 naar het plaatsje Seabrook in North Carolina om vakantie te vieren met haar familie. Daar ontmoet ze de knappe Noah Calhoun die vanaf t eerste moment verliefd op haar is. Ondanks hun zeer verschillende achtergronden beleven de twee een zomer vol hartstocht en passie. Maar als Allie verhuist en Noah zich aanmeldt bij het leger lijkt de liefde voorgoed voorbij.', 'https://www.youtube.com/embed/BjJcYdEOI0k', 'notebook.jpg', 'normaal'),
    ('Dirty Dancing', 7.0, 'romantiek', 'Engels' , '100 minuten', '3-12-1987', 'Zomer 1963. De 17-jarige naïeve Frances Baby Houseman brengt met haar ouders en haar oudere zus Marjorie de vakantie door op het resort van het hotel Catshill. Baby ontmoet daar de verveelde dansleraar van het hotel Johnny Castle. Johnny brengt Baby dirty dancing bij, ze wordt zijn pupil en ze zet haar eerste schreden in de liefde.', 'https://www.youtube.com/embed/eIcmQNy9FsM', 'Dirty_Dancing.jpg', 'normaal'),
    ('The vow', 6.8, 'romantiek', 'Engels' , '104 minuten', '3-5-2012', 'Een vrouw raakt na een auto-ongeval in een coma. Als ze terug bij bewustzijn komt, is ze haar geheugen en alle herinneringen van de voorbije vijf jaar kwijt. Ze heeft er ook geen flauw benul van wie haar man is. Hij zal opnieuw haar hart voor zich moeten winnen.', 'https://www.youtube.com/embed/tdF01cA7jOE', 'the_vow.jpg', 'normaal'),
    ('He is all that', 4.4, 'romantiek', 'Engels' , '88 minuten', '25-8-2021', 'Nieuwe interpretatie van de originele tienerfilm Shes All That uit 1999. Influencer Padgett Sawyer (Addison Rae) neemt de uitdaging aan om de antisociale Cameron Kweller (Tanner Buchanan) tot koning van het eindejaarsbal te kronen. Het wordt allemaal wat gecompliceerder wanneer ze gevoelens voor hem begint te ontwikkelen...', 'https://www.youtube.com/embed/6HaVRjpU_ro', 'hes_al_that.jpg', 'netflix'),
    ('The kissing booth', 5.9, 'romantiek', 'Engels' , '105 minuten', '11-3-2018', 'Elle Evans, een zestienjarig meisje, is populair en feest graag maar is nog nooit gekust. De zaken nemen een wending die Elle nooit had verwacht wanneer ze tijdens carnaval terechtkomt bij een attractie genaamd kissing booth. Daar staat ze plots oog in oog met de jongen waar ze al lang verliefd op is - de broer van haar beste vriend, Noah. Elle wordt vervolgens meegesleept in een romance met de coolste jongen op school.', 'https://www.youtube.com/embed/4CkmmcgMOqQ', 'the_kissing_booth.jpg', 'netflix'),
    ('Tall Girl', 5.2, 'romantiek', 'Engels' , '101 minuten', '13-9-2019', 'De 16-jarige Jodi, die 1,90 m lang is, en heeft moeite met haar lengte. Ze moet leren te omarmen wat haar anders maakt. Ze heeft nog nooit een vriendje gehad. Dat zou kunnen veranderen wanneer er een lange uitwisselingsstudent op haar school komt...', 'https://www.youtube.com/embed/NfpXeLVzJIw', 'tall_girl.jpg', 'netflix'),
    ('Let it snow ', 5.8, 'romantiek', 'Engels' , '93 minuten', '8-11-2019', 'Wanneer een klein dorpje op kerstavond ondergesneeuwd wordt, veranderen de vriendschappen en liefdeslevens van een groep middelbare scholieren. Een gestrande popster, een groep competitieve dansers, een mysterieuze vrouw gewikkeld in aluminiumfolie en een episch feest in de lokale Waffle Town. De volgende ochtend zal niets meer hetzelfde zijn...', 'https://www.youtube.com/embed/pitxxQYZcug', 'let_it_snow.jpg', 'netflix'),
    ('Love hard', 6.3, 'romantiek', 'Engels' , '104 minuten', '5-11-2021', 'De hopeloze, maar eeuwig single blijvende journaliste Natalie uit Los Angeles denkt dat de dingen beginnen te verbeteren als ze Tag, een dromerige jongen van de oostkust, aan de haak slaat via een dating-app. Ze besluit hem voor de feestdagen te verrassen. Maar dan ontdekt ze dat ze wordt opgelicht door de jeugdvriend van Tag, Josh, die al evenveel pech heeft in de liefde. Toch doet ze een poging om weer verliefd te worden.', 'https://www.youtube.com/embed/3boMRfx6cjE', 'love_hard.jpg', 'netflix'),
    ('Oceans eleven', 7.7, 'triller', 'Engels', '116 minuten', '7-12-2001', 'De crimineel Danny Ocean komt na een gevangenisstraf van vier jaar eindelijk vrij. Hij besluit zijn herwonnen vrijheid meteen goed te gebruiken door drie casinos in Las Vegas te beroven. Om deze bijna onmogelijke klus te klaren, verzamelt hij een grote crew professionals om zich heen. Al snel blijkt dat er voor Danny meer op het spel staat dan alleen maar geld: zijn ex-vrouw Tess is namelijk de nieuwe vriendin van Terry Benedict, de eigenaar van de casinos die hij wil beroven.', 'https://www.youtube.com/embed/imm6OR605UI', 'ocean_eleven.jpg', 'normaal'),
    ('Fight club', 8.8, 'triller', 'Engels', '139 minuten', '4-11-1999', 'Een man die voor een verzekeringsmaatschappij stad en land afreist als schade-expert, lijdt aan chronische slapeloosheid. Hij krijgt van een arts echter geen medicatie, maar een snerende opmerking: Ga maar eens kijken bij een praatgroep voor terminaal zieken, dan zal je zien wat pijn écht inhoudt. De man neemt het serieus en bezoekt een praatgroep, waar men aanneemt dat hij ook terminaal ziek is. Hij vindt er een uitlaatklep en bezoekt vervolgens ook andere praatgroepen. Bij één van die bezoeken treft hij de mysterieuze Marla Singer. Als hij op een dag thuis komt van een vlucht, blijkt er een explosie in zijn appartement te zijn geweest. Hij belt Tyler Durden, een extravagante man die hij net daarvoor in het vliegtuig ontmoet heeft. Tyler neemt op, en ze gaan samen een biertje drinken. Tyler blijkt er nogal eigenzinnige ideeën op na te houden.', 'https://www.youtube.com/embed/qtRKdVHc-cE', 'fight_club.jpg', 'normaal'),
    ('Rebecca', 6.0, 'triller', 'Engels', '121 minuten', '16-10-2020', 'Een jonge vrouw van eenvoudige komaf trouwt met een gedistingeerde kasteelheer, Maxim de Winter genaamd. Hun huwelijksgeluk lijkt van korte duur door de herinnering aan Rebecca, de vorige vrouw van Maxim. De oude geliefde van Maxim kwam onder duistere omstandigheden om het leven, maar haar geest lijkt nog altijd rond te waren.', 'https://www.youtube.com/embed/LFVhB54UqvQ', 'rebecca.jpg', 'normaal'),
    ('End of the road', 4.7, 'triller', 'Engels', '89 minuten', '9-9-202', 'Een reis per auto door het land verandert in een nachtmerrie voor de onlangs weduwe geworden Brenda, haar twee kinderen en haar broer Reggie. Na getuige te zijn geweest van een brute moord, bevindt de familie zich in het vizier van een mysterieuze moordenaar. Ze zijn alleen in de woestijn van New Mexico en afgesneden van elke hulp. Brenda wordt weldra in een dodelijk gevecht getrokken om haar familie in leven te houden.', 'https://www.youtube.com/embed/202KkD12AuI', 'end_of_the_road.jpg', 'normaal'),
    ('A simple favor', 6.8, 'triller', 'Engels', '117 minuten', '27-9-2018', 'Stephanie is moeder en vlogger. Haar beste vriendin, Emily, vraagt haar om haar zoontje Nicky mee te nemen uit school. Stephanie vangt wel vaker de beide jongetjes op als de succesvolle Emily voor haar werk van huis is. Maar deze keer komt Emily haar zoontje niet ophalen en ze reageert ook niet op telefoontjes. Stephanie probeert de waarheid te achterhalen van de plotselinge verdwijning van haar vriendin Emily uit hun kleine stadje. Ze wordt hierbij bijgestaan door Emilys echtgenoot Sean.', 'https://www.youtube.com/embed/rAqMlh0b2HU', 'a_simple_favor.jpg', 'normaal'),
    ('The weekend away', 5.6, 'triller', 'Engels' , '89 minuten', '3-3-2022', 'Kate en Orla zijn twee beste vriendinnen die persoonlijke tegenslagen hebben overwonnen. Wat het leven hen ook brengt, ieder keer kijken ze weer uit naar jaarlijkse weekendje weg. Dit jaar zijn ze op reis naar Kroatië. Alles is perfect totdat Orla wakker wordt en Kate vermist wordt. Met slechts een vage herinnering aan de vorige nacht en de politie die niet helpt, onthult haar verwoede zoektocht verwoestende geheimen dichter bij huis.', 'https://www.youtube.com/embed/XpfjN-7Hf-8', 'The_Weekend_Away_film_poster.png', 'netflix'),
    ('Mother/Android', 4.9, 'triller', 'Engels' , '110 minuten', '17-12-2021', 'Wanneer hun vaderland verstrikt raakt in een oorlog met betrekking tot artificiële intelligentie, poogt Georgia met haar vriendje Sam het conflict te ontvluchten. Het stel hoopt een veilig onderkomen te vinden, zeker nu in de laatste dagen voordat ze op bevallen staat. De twee komen terecht bij de vesting van de rebellerende androïden.', 'https://www.youtube.com/embed/JRl214mDZ-g', 'mother_android.jpg', 'netflix'),
    ('Bird box', 6.6, 'triller', 'Engels' , '123 minuten', '14-12-2018', 'In de nasleep van een wereldwijde dreiging van onbekende oorsprong moet Malorie de kracht vinden om haar kinderen veilig te kunnen stellen. Ze vlucht met ze weg via een verraderlijke rivier, op zoek naar een veilig onderkomen. Vanwege dodelijke onzichtbare krachten moeten ze hun weg blind vervolgen.', 'https://www.youtube.com/embed/o2AsIXSh2xo', 'Bird_Box.png', 'netflix'),
    ('Army of thieves', 6.4, 'triller', 'Engels', '127 minuten', '29-10-2021', 'We volgen Ludwig Dieter in de gebeurtenissen vóór Army of the Dead. Terwijl de zombie-uitbraak nog in een vroeg stadium zit, houdt Dieter zich bezig met kluizen kraken. Door een mysterieuze vrouw wordt hij benaderd om een overval te plegen. Hij stemt toe en voegt zich bij de meest gezochte criminelen op de lijst van Interpol, buitenbeentjes die graag in zijn voetsporen willen treden.', 'https://www.youtube.com/embed/Ith2WetKXlg', 'army_of_thieves.jpg', 'netflix'),
    ('Spiderhead', 5.4, 'triller', 'Engels' , '106 minuten', '11-6-2022', 'Escape from Spiderhead speelt zich af in de nabije toekomst, waarin twee veroordeelden in een voorziening wonen dat gerund wordt door een dokter. Deze dokter voert experimenten uit op de gevangenen met drugs die de emoties aantasten. De veroordeelden stemmen in met de situatie, in ruil voor een vermindering van hun celstraf. Maar na de proeven gaan ze zelf op onderzoek uit.', 'https://www.youtube.com/embed/BfsNfFoA0J0', 'Spiderhead.jpg', 'netflix'),
    ('Pitch perfect', 7.1, 'comedy', 'Engels', '112 minuten', '3-1-203', 'Twee concurrerende a capella zanggroepen, de Barden Bellas (allemaal meisjes) en de Barden Treblemakers (allemaal jongens), strijden om het “International Championship of Collegiate A Capella” (ICCA) te winnen, dat elk jaar in de lente gehouden wordt op Carnegie Hall. De Barden Bellas krijgen echter te maken met persoonlijke conflicten: hun zangeres Chloe heeft stemproblemen, velen van hen zijn afgestudeerd, en hun leider Aubrey probeert wanhopig te groep bijeen te houden. Dan wordt de eerstejaars studente Beca Mitchell in de groep opgenomen. De rebelse Beca heeft goede ideeën, maar deze worden routinematig neergesabeld door Aubrey. Ondertussen probeert Beca niet verliefd te worden op Jesse, een nieuw lid van de Treblemakers.', 'https://www.youtube.com/embed/X_bbBSYwlY8', 'pitch_perfect.jpg', 'normaal'),
    ('28 Days', 6.1, 'comedy', 'Engels', '103 minuten', '22-6-2000', 'Gwen Cummings is een succesvolle New Yorkse schrijfster, die er een wild uitgaanspatroon op na houdt. Ze duikt op in alle trendy clubs en is in beschonken toestand te vinden op elk feestje. Haar leven neemt een radicale wending wanneer ze op het huwelijksfeest van haar zus Lily volledig door het lint gaat. Ze wordt veroordeeld tot een afkickkuur van 28 dagen in een ontwenningskliniek. Gwen moet zich houden aan een strikt schema vol regels en afspraken. Ze komt al snel in conflict met zichzelf en alle andere patiënten. Maar de cynische Gwen wil zich niet neerleggen bij de normale gang van zaken in het instituut.', 'https://www.youtube.com/embed/y7RXmrlGe-k', '28_days.jpg', 'normaal'),
    ('Sex and the city', 5.7, 'comedy', 'Engels', '148 minuten', '12-6-2008', 'Sex and the City volgt de levens van vier vriendinnen in Manhattan, New York. Columniste Carrie (Sarah Jessica Parker), advocate Miranda (Cynthia Nixon), zakenvrouw Samantha (Kim Cattrall) en de brave Charlotte (Kristin Davis) zouden genoeg aan zichzelf moeten hebben, maar in plaats daarvan verdiepen de dames zich voortdurend in de levens van elkaar en proberen samen de juiste weg te vinde', 'https://www.youtube.com/embed/g9Mx2OLnoGI', 'sex_and_the_city.jpg', 'normaal'),
    ('Mamma Mia!', 6.5, 'comedy', 'Engels', '108 minuten', '17-9-208', 'Donna, een onafhankelijke, single moeder heeft een hotelletje op een idyllisch Grieks eiland. Ze staat op het punt om Sophie, de pittige dochter die ze alleen heeft grootgebracht, los te laten. Voor Sophies huwelijk heeft Donna haar twee oude hartsvrienden uitgenodigd: de praktische en no-nonsense Rosie en de rijke, meermalen gescheiden Tanya van haar vroegere backing-band Donna and the Dynamos. Maar Sophie heeft in het geheim zelf drie gasten uitgenodigd. Ze is op zoek gegaan naar de identiteit van haar vader die haar naar het altaar moet begeleiden en ze haalt drie mannen uit Donnas verleden naar het mediterrane paradijs waar ze 20 jaar daarvoor waren geweest. In de 24 chaotische, magische uren bloeit er nieuwe liefde op en oude romances vatten opnieuw vlam op dit weelderige eiland vol mogelijkheden.', 'https://www.youtube.com/embed/lkN-A00WLYE', 'mamma_mia.jpg', 'normaal'),
    ('Paddington', 7.3, 'comedy', 'Engels', '95 minuten', '11-2-2015', 'Een jonge Peruaanse beer, die een passie heeft voor alles wat Brits is, vertrekt naar Londen waar hij op zoek gaat naar een woning. Wanneer hij eenzaam en verloren op Paddington Station belandt, begint hij te beseffen dat het leven in de stad niet is wat hij zich ervan had voorgesteld. Maar dan ontmoet hij de vriendelijke familie Brown, die het label om zijn nek leest ("Pas alsjeblieft op deze beer, dankjewel") en hem een tijdelijk onderkomen aanbiedt.', 'https://www.youtube.com/embed/CxeBdrGGU8U', 'paddington.jpg', 'normaal'),
    ('Yesterday', 6.8, 'comedy', 'Engels', '116 minuten', '27-6-2019', 'Yesterday volgt het leven van een worstelende muzikant. Hij komt erachter dat hij van de ene op de andere dag de enige persoon op aarde is die zich de Beatles kan herinneren. Met de kennis van al hun muziek is hij vervolgens relevanter dan ooit.', 'https://www.youtube.com/embed/Ry9honCV3qc', 'yesterday_.jpg', 'netflix'),
    ('Little', 5.5, 'comedy', 'Engels', '109 minuten', '11-4-2019', 'Little volgt een vrouw die de kans krijgt om haar jeugd te herbeleven. Ze gaat terug in de tijd naar haar jongere zelf waar ze nog een zorgeloos leven leidde. Een leven waar langzamerhand de druk van volwassenheid om de hoek komt kijken en het haar destijds te veel werd.', 'https://www.youtube.com/embed/Qz65no3WnJk', 'little.jpg', 'netflix'),
    ('Set it up', 6.5, 'comedy', 'Engels', '105 minuten', '15-6-2018', 'Harper en Charlie zijn twee assistenten werkzaam in New York. Hun werkgevers zijn beiden verslaafd aan hun werk en maken het hun personeel weleens lastig. Harper en Charlie vinden hierop een oplossing en organiseren een date voor de bazen. Tijdens het plannen en uitvoeren van hun idee beseffen de assistenten dat er ook romantiek voor hen in de lucht hangt.', 'https://www.youtube.com/embed/X-eRc9PF3TU', 'set_it_up.jpg', 'netflix'),
    ('The f**k it list', 5.1, 'comedy', 'Engels', '103 minuten', '1-7-2020', 'Brett Blackmore is een high school leerling en een echte studiebol. Zijn leven valt in duigen door een misgelopen grap die hij heeft uitgehaald. Nu deelt hij een lijst van dingen die hij liever anders had gedaan en wellicht nog kan veranderen. Deze lijst gaat viraal en komt terecht bij veel jongeren, wat voor ophef zorgt.', 'https://www.youtube.com/embed/eyW5yoMpBtM', 'the_fack_it_list.jpg', 'netflix'),
    ('Dude', 5.1, 'comedy', 'Engels', '97 minuten', '20-4-2018', 'Vier beste vriendinnen zitten voor de laatste twee weken op de middelbare school. Ze krijgen te maken met verlies en grote levensveranderingen. Ze ervaren dit met een vreemde combinatie van verdriet en nostalgie terwijl ze hun verlies trachten te begrijpen.', 'https://www.youtube.com/embed/eIBQaDlR0tA', 'dude.jpg', 'netflix'),
    ('Get out', 7.7, 'horror', 'Engels', '104 minuten', '24-2-2017', 'Rose nodigt Chris uit om een weekend door te brengen met haar ouders. In eerste instantie denkt Chris dat het vreemde gedrag van de familie te maken heeft met hun interraciale relatie, maar gedurende het weekend ontdekt Chris de waarheid.', 'https://www.youtube.com/embed/DzfpyUB60YY','getout.jpg', 'normaal'),
    ('SAW VI', 8.2,'horror', 'Engels', '90 minuten' ,'3-1-2008', 'Wanneer er een videoband opduikt van Jigsaw gaan er alarmbellen af, want hij en Amanda zijn dood. Het spel is echter nog lang niet aan zijn eind. Twee FBI agenten proberen te puzzel op te lossen.', 'https://www.youtube.com/embed/hQ7JF5JZkoo', 'saw.jpg','normaal'),
    ('Final destination', 5.5,'horror', 'Engels', '120 minuten', '16-3-2000', 'Alex moet mee in een Boeing 747, maar hij ziet voor zich dat het toestel zal ontploffen. In paniek wordt hij, en enkele anderen, uit het toestel gezet en enkele momenten later ziet hij het toestel opstijgen en veranderen in een vuurbal. ','https://www.youtube.com/embed/lP4Psj7d1ZI','final.jpg' ,'normaal'),
    ('Resident Evil: Extinction', 7.2, 'horror', 'Engels', '130 minuten', '3-9-2007', 'Overlevenden van Raccoon City reizen door de Nevada-woestijn, hopende dat ze Alaska kunnen bereiken. Alice sluit zich bij deze karavaan aan. Samen binden ze de strijd aan tegen de hordes zombies en de kwaadaardige Umbrella Corporation.', 'https://www.youtube.com/embed/eDMjVARsCOk','residentevil2.jpg' ,'normaal'),
    ('V/H/S', 6, 'horror', 'Engels', '112 minuten','3-2-2012', 'Een groep criminelen wordt ingehuurd om in een vervallen huis een VHS tape op te halen. Ze worden echter geconfronteerd met gruwelijke videos terwijl ze op zoek zijn naar de juiste tape', 'https://www.youtube.com/embed/opnSln8Ue2U','vhs.jpg', 'normaal'),
    ('Top Gun: Maverick', 9.5, 'actie', 'Engels', '125 minuten', '2-6-2022', 'Na meer dan dertig jaar zit Maverick nog steeds op zijn plek bij de marine. Ineens moet hij de beste piloten voorbereiden om de moeilijkste missie ooit. Gaat het ze lukken? En onthoud; het gaat niet om het vliegtuig, maar om de piloot.', 'https://www.youtube.com/embed/giXco2jaZ_4','topgun.jpg', 'normaal'),
    ('The Batman', 8.7, 'actie', 'Engels', '154 minuten', '4-3-2022', 'Wanneer de Riddler, een seriemoordenaar, belangrijke politieke figuren in Gotham begint te vermoorden, wordt Batman gedwongen de verborgen corruptie van de stad te onderzoeken en de betrokkenheid van zijn familie in twijfel te trekken.', 'https://www.youtube.com/embed/mqqft2x_Aa4','batman.jpg', 'normaal'),
    ('Nobody', 8.9, 'actie', 'Engels', '138 minuten', '26-3-2021', 'Als er op een nacht twee dieven bij hem inbreken, weigert Hutch zichzelf en zijn gezin te verdedigen, in de hoop ernstig geweld te voorkomen. Na het incident ontwaakt er een woede in hem die hij lange tijd heeft onderdrukt.', 'https://www.youtube.com/embed/_2un1aU7mT0','nobody.jpg', 'normaal'),
    ('Fast & Furious 9', 8.6, 'actie', 'Engels', '136 minuten', '8-13-2021', 'Dominic Toretto leidt een rustig bestaan samen met Letty en hun zoontje Brian. Beiden weten ze dat het gevaar altijd op de loer ligt. Dit keer komt de dreiging uit Dom’s verleden. ', 'https://www.youtube.com/embed/d1j5NVYVIok','fast.jpg', 'normaal'),
    ('Rampage: Big Meets Bigger', 9, 'actie', 'Engels', '120 minuten', '12-4-2022', 'De film volgt Davis Okoye, een primatoloog die een bijzondere band heeft met een gorilla genaamd George. Wanneer een experiment mislukt, verandert George in een reusachtige monster. Okoye moet nu samenwerken met een bioloog om George te stoppen.', 'https://www.youtube.com/embed/rDgtKfshJ50','rampage.jpg', 'normaal'),
    ('Interstellar', 9, 'sci-fi', 'Engels', '173 minuten', '2-4-2014', 'Onze tijd op aarde lijkt voorbij, dus begint een groep ontdekkingsreizigers aan de belangrijkste missie uit de geschiedenis van de mens. Ze gaan op zoek naar een plek in de ruimte waar de mensheid kan voortbestaan.', 'https://www.youtube.com/embed/zSWdZVtXT7E','interstellar.jpg', 'normaal'),
    ('The Matrix Resurrections', 7.8, 'sci-fi', 'Engels', '142 minuten', '9-2-2022', 'Neo zit gevangen in de wereld van de Matrix, maar heeft dit niet door. Hij besluit een blauwe pil te nemen en naar de therapeut te gaan. Alles wordt anders als een man Neo verteld dat zijn realiteit nep is. Wat is zijn leven dan wel?', 'https://www.youtube.com/embed/9ix7TUGVYIo','matrix.jpg', 'normaal'),
    ('War for the Planet of the Apes', 8.6, 'sci-fi', 'Engels', '140 minuten', '8-4-2017', 'Caesar en zijn apen worden in een dodelijk conflict gedwongen. Wanneer Caesar en de kolonel oog in oog met elkaar staan, nemen ze het tegen elkaar op in een episch gevecht dat het lot van beide soorten zal bepalen. ', 'https://www.youtube.com/embed/qxjPjPzQ1iU','apes.jpg', 'normaal'),
    ('Pokémon Detective Pikachu', 8.6, 'sci-fi', 'Engels', '104 minuten', '5-8-2019', 'Wanneer top privé detective Harry Goodman mysterieus vermist raakt, gaat zijn zoon Tim naar hem opzoek. Hij krijgt hulp van Detective Pikachu . Het duo is vastberaden mysterie rondom Harry op te lossen. ', 'https://www.youtube.com/embed/1roy4o4tqQM','pikachu.jpg', 'normaal'),
    ('Ready Player One', 8.6, 'sci-fi', 'Engels', '140 minuten', '8-5-2018', 'De wereld staat op het punt van instorten. De mensen zoeken hun redding in OASIS, een VR universum, gemaakt door Halliday. Wanneer hij overlijdt, laat hij een fortuin na aan degene die het digitale Easter Egg vindt binnen OASIS. ', 'https://www.youtube.com/embed/cSp1dM2Vj48','playerone.jpg', 'normaal'),
    ('Inception', 8.8, 'sci-fi', 'Engels', '148 minuten', '3-10-2010', 'Dom Cobb (Leonardo DiCaprio) is een handige dief, gespecialiseerd in het stelen van waardevolle geheimen in de droomstaat van een mens, wanneer de geest het kwetsbaarst is. Dankzij die zeldzame gave is Cobb een veelgevraagde speler in de wereld van bedrijfsspionage, maar is hij ook in vele landen voortvluchtig. Daarnaast is hij alles kwijt wat hem ooit dierbaar was. Cobb krijgt echter de kans zijn oude leven weer terug te krijgen, in ruil voor het uitvoeren van een vrijwel onmogelijke taak: Cobb en zijn team van specialisten moeten deze keer geen idee stelen, maar het in iemands brein plaatsen. Als ze hierin slagen, zou het de perfecte misdaad zijn.', 'https://www.youtube.com/embed/YoHD9XEInc0','inception.jpg', 'netflix'),
    ('The Matrix', 8.7, 'sci-fi', 'Engels', '136 minuten', '31-3-1999', 'Neo (Keanu Reeves) is een programmeur die in zijn vrije tijd graag computerspelletjes speelt. Hij heeft een vriendin, Trinity (Carrie-Anne Moss), en een baan bij een softwarebedrijf. Neo is echter niet wie hij denkt te zijn. Hij is eigenlijk Thomas Anderson, een hacker die in het dagelijks leven als programmeur werkt.', 'https://www.youtube.com/embed/vKQi3bBA1y8','matrix1.jpg', 'netflix'),
    ('Back to the future', 8.5, 'sci-fi', 'Engels', '111 minuten', '2-7-1985', 'Marty McFly (Michael J. Fox) is een doorsnee tiener die bevriend is met de gekke uitvinder Doc Brown (Christopher Lloyd). Doc ontwerpt een tijdmachine waardoor Marty per ongeluk wordt teruggezonden naar 1955. Hier ontmoet hij zijn ouders die in deze tijd natuurlijk nog veel jonger zijn. Hij verstoort de ontmoeting met zijn ouders en moet hier alles aan doen om hen weer te herenigen.', 'https://www.youtube.com/embed/qvsgGtivCgs','future.jpg', 'netflix'),
    ('The Matrix Reloaded', 7.2, 'sci-fi', 'Engels', '138 minuten', '15-5-2003', 'De Matrix is een computerprogramma dat de mensheid al duizenden jaren in een illusie houdt. De Matrix is een wereld die op het eerste gezicht lijkt op de echte wereld, maar in werkelijkheid een computerprogramma is. De Matrix wordt beheerd door een supercomputer die de mensheid als een energiebron gebruikt. De Matrix is een soort droomwereld waarin de mensheid wordt gehouden in slaap door een stroom van elektriciteit die via de hersenen wordt gevoerd. De Matrix is een computerprogramma dat de mensheid al duizenden jaren in een illusie houdt. De Matrix is een wereld die op het eerste gezicht lijkt op de echte wereld, maar in werkelijkheid een computerprogramma is. De Matrix wordt beheerd door een supercomputer die de mensheid als een energiebron gebruikt. De Matrix is een soort droomwereld waarin de mensheid wordt gehouden in slaap door een stroom van elektriciteit die via de hersenen wordt gevoerd.', 'https://www.youtube.com/embed/kYzz0FSgpSU','matrixre.jpg', 'netflix'),
    ('The Matrix Revolutions', 6.7, 'sci-fi', 'Engels', '129 minuten', '5-11-2003', 'De Matrix is een computerprogramma dat de mensheid al duizenden jaren in een illusie houdt. De Matrix is een wereld die op het eerste gezicht lijkt op de echte wereld, maar in werkelijkheid een computerprogramma is. De Matrix wordt beheerd door een supercomputer die de mensheid als een energiebron gebruikt. De Matrix is een soort droomwereld waarin de mensheid wordt gehouden in slaap door een stroom van elektriciteit die via de hersenen wordt gevoerd.', 'https://www.youtube.com/embed/hMbexEPAOQI','matrixrev.jpg', 'netflix'),
    ('Django Unchained', 8.4, 'actie', 'Engels', '165 minuten', '18-1-2013', 'Django (Jamie Foxx) is een slaaf die samen met zijn vrouw Broomhilda (Kerry Washington) wordt gekocht door de sadistische plantage-eigenaar Calvin Candie (Leonardo DiCaprio). Django wordt door Candie verkocht aan de schurk Dr. King Schultz (Christoph Waltz), een Duitse tandarts die zich voordoet als een slaafjager. Schultz wil Candie ontmaskeren als een slavenhandelaar en Django helpt hem daarbij. In ruil daarvoor belooft Schultz Django te helpen zijn vrouw terug te vinden.', 'https://www.youtube.com/embed/_iH0UBYDI4g', 'django_unchained.jpg', 'netflix'),
    ('Spider-Man: No Way Home', 8.3, 'actie', 'Engels', '148 minuten', '16-12-2021', 'Spider-Man is ontmaskerd en kan zijn normale leven niet langer gescheiden houden van zijn gevaarlijke leven als superheld. Als hij Doctor Strange om hulp vraagt, staat er meer op het spel dan ooit tevoren. Dit dwingt hem onder ogen te zien wat het nu echt betekent om Spider-Man te zijn.', 'https://www.youtube.com/embed/JfVOs4VSpmA', 'spiderman_no_way_home.jpg', 'netflix'),
    ('warrior', 8.2, 'actie', 'Engels', '140 minuten', '6-10-2011', 'Een gepensioneerde staalarbeider en voormalig bokser (Nick Nolte) heeft een groot alcohol probleem. Dit heeft er onder meer voor gezorgd dat zijn gezin uiteen is gevallen. Op zekere dag komt hij tot inkeer en versobert op berouwvolle wijze zijn levenswandel. Zijn jongste zoon (Tom Hardy) accepteert vervolgens maar al te graag zijn aanbod om hem te trainen als worstelaar. Zijn broer (Joel Edgerton) heeft het echter niet zo op zijn vader en werkt aan een eigen loopbaan als worstelaar. Beide broers bereiden zich nu voor op hetzelfde toernooi.', 'https://www.youtube.com/embed/I5kzcwcQA1Q', 'warrior.jpg', 'netflix'),
    ('Mad Max: Fury Road', 8.1, 'actie', 'Engels', '120 minuten', '14-5-2015', 'Achtervolgd door zijn turbulente verleden, is Mad Max (Tom Hardy) ervan overtuigd dat hij alleen kan overleven als hij in zijn eentje opereert. Toch komt hij in contact met een groep die in een oorlogsvoertuig, bestuurd door de imperator Furiosa, door het Wasteland trekt. De groep is op de vlucht voor de tiran Immortan Joe, van wie iets onvervangbaars is gestolen. De woedende krijgsheer stuurt al zijn bendes achter de rebellen aan, wat uitmondt in een meedogenloze, bloedstollende Road War.', 'https://www.youtube.com/embed/hEJnMQG9ev8', 'mad_max.jpg', 'netflix'),
    ('Gladiator', 8.5, 'actie', 'Engels', '156 minuten', '18-5-2000', 'In 180, de gloriedagen van het Romeinse Imperium, overlijdt de grote keizer Antonius Marcus Aurelius (Richard Harris). Hij wordt opgevolgd door zijn wrede zoon Commodus (Joaquin Phoenix). Deze laat de beste strijders vechten om het volk te vermaken. Eén van deze gladiatoren, de door Commodus tot slaaf gedegradeerde generaal Maximus (Russell Crowe), weet zich te verheffen tot de held van het volk. Zijn doel is de beste gladiator aller tijden te worden om zich zo te wreken op keizer Commodus.', 'https://www.youtube.com/embed/owK1qxDselE', 'gladiator.jpg', 'netflix'),
    ('Jaws', 8.1, 'horror', 'Engels', '124 minuten', '20-6-1975', 'De zomervakantie is begonnen en de badgasten van Amity Island, een klein eiland voor de kust van New England, zijn in opperste staat van paraatheid. De zee is echter niet zo vriendelijk als de zon. Een grote witte haai heeft het eiland ontdekt en begint een bloedige jacht op de badgasten. De politie en de badgasten proberen de haai te vangen, maar de haai is veel te slim voor hen. De enige die de haai kan stoppen is de jonge politieagent Brody (Roy Scheider).', 'https://www.youtube.com/embed/U1fu_sA7XhE', 'jaws.jpg','netflix'),
    ('The Shining', 8.4, 'horror', 'Engels', '146 minuten', '13-6-1980', 'Jack Torrance (Jack Nicholson) is een schrijver die in de winter met zijn vrouw Wendy (Shelley Duvall) en zoon Danny (Danny Lloyd) naar het afgelegen Overlook Hotel gaat om er als winterseizoenarbeider te werken. Het hotel is gesloten voor het publiek en wordt alleen gebruikt als een rustplaats voor de winterseizoenarbeiders. Jack is een schrijver die zijn inspiratie nodig heeft en hoopt dat hij die in het hotel zal vinden. Wendy en Danny zijn echter niet zo blij met hun nieuwe woonplaats. Danny heeft namelijk de gave om de toekomst te zien en ziet in het hotel een gevaarlijke plek.', 'https://www.youtube.com/embed/5Cb3ik6zP2I','shining.jpg', 'netflix'),
    ('Split', 7.3, 'horror', 'Engels', '116 minuten', '19-1-2017', 'Kevin (James McAvoy), die lijdt aan een meervoudige persoonlijkheidsstoornis, wordt door een van de persoonlijkheden die in hem leeft, aangezet tot het ontvoeren van drie tienermeisjes waaronder de scherpzinnige Casey (Anya Taylor-Joy). Samen met zijn vertrouwde psychiater Dr. Fletcher (Betty Buckley) moet Kevin een overlevingsstrijd voeren met alle persoonlijkheden die in hem schuilen.', 'https://www.youtube.com/embed/84TouqfIsiI','split.jpg','netflix'),
    ('Dead of Night', 7.6, 'horror', 'Engels', '102 minuten', '17-9-2009', 'In deze film van vier regisseurs vertellen gasten tijdens een etentje sinistere verhalen aan een man met déjà vus.', 'https://www.youtube.com/embed/8UCJz617E8s','dead.jpg', 'netflix'),
    ('A Quiet Place', 7.5, 'horror', 'Engels', '90 minuten', '12-4-2018', 'In A Quiet Place moet een gezin sluipend, lippend en gebarend door het leven gaan. Het minste geluid kan hen overleveren aan geheimzinnige wezens die jagen op het gehoor. In het volle besef dat zelfs een voetstap of gefluister dodelijk kunnen zijn, proberen Evelyn (Emily Blunt) en Lee (John Krasinski) een manier te vinden om hun kinderen koste wat kost te beschermen en hun loerende vijanden te overwinnen.', 'https://www.youtube.com/embed/WR7cc5t7tv8','quiet.jpg', 'netflix');
