DROP DATABASE IF EXISTS `Fresh_tomatoes`;

CREATE DATABASE `Fresh_tomatoes`;

USE `Fresh_tomatoes`;

DROP TABLE IF EXISTS `gebruikers`;

CREATE TABLE gebruikers (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    email VARCHAR(30) NOT NULL,
    password varchar(30) NULL,
    name VARCHAR(30) NOT NULL
);

USE `Fresh_tomatoes`;
DROP TABLE IF EXISTS `favoriet`;
CREATE TABLE `favoriet` (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    user_id VARCHAR(100) NOT NULL,
    film_id VARCHAR(100),
    serie_id varchar(100)
);

USE `Fresh_tomatoes`;
DROP TABLE IF EXISTS `adminlogin`;
CREATE TABLE IF NOT EXISTS `adminlogin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(100) NOT NULL,
  `password` varchar(100) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


USE `Fresh_tomatoes`;
DROP TABLE IF EXISTS `film`;
CREATE TABLE `film` (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    titel VARCHAR(100) NOT NULL,
    score VARCHAR(100) NOT NULL,
    genre varchar(100) NOT NULL,
    taal varchar(100) NOT NULL,
    duur VARCHAR(100) NOT NULL,
    datum VARCHAR(100) NULL,
    beschrijving TEXT NOT NULL,
    youtube_trailer_id VARCHAR(100) NOT NULL,
    foto varchar(100) NOT NULL,
    type ENUM('netflix', 'normaal')
);

USE `Fresh_tomatoes`;
DROP TABLE IF EXISTS `fverwacht`;
CREATE TABLE `fverwacht` (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    titel VARCHAR(100) NOT NULL,
